<?phpclass Test{	private $cadena;//privado	public $entero; //publico	var $flotante;  //publico		//CONSTRUCTOR	public function __construct()	{		$this->cadena = $this->FormatearCadena("valor inicial");		$this->entero = 1;		$this->flotante = 0.0;	}		//METODO PUBLICO DE INSTANCIA	public function Mostrar()	{		return $this->cadena." - ".$this->entero." - ".$this->flotante;	}		//METODO PRIVADO DE INSTANCIA	private function FormatearCadena($cadena)	{		return ucwords($cadena);	}		//METODO DE CLASE	public static function MostrarTest($obj)	{		return $obj->Mostrar();	}}