<?php//DECLARO CLASE ABSTRACTAabstract class ClaseAbstracta{	//ATRIBUTOS	protected $atributo;		//CONSTRUCTOR	public function __construct($valor)	{		$this->atributo = $valor;	}		//METODO ABSTRACTO	public abstract function MetodoAbstracto();		//METODO NO ABSTRACTO	public function getAtributo()	{		return $this->atributo;	}}//CLASE QUE DERIVA DE LA CLASE ABSTRACTAclass Clase extends ClaseAbstracta{	//ATRIBUTOS	public $otroAtributo;		//CONSTRUCTOR	public function __construct($valor, $otroValor)	{		parent::__construct($valor);		$this->otroAtributo = $otroValor;	}		//IMPLEMENTO METODO ABSTRACTO	public function MetodoAbstracto(){		return "<br/>M&eacute;todo Abstracto";	}}?>